sources.scss/_base.scss:
body {
  display: grid;
  grid-template-rows: auto auto 48px;
  height: 100vh;
  font-family: Raleway;

}

sources.scss/_utility.scss:
p {
  font-size: 20px;
}
a {
  color: #ee7600;
  text-decoration: none;
  &:hover {
    color: white;
    text-decoration: none;
    background-color: #ee7600;
  }
}
.h1 {
  font-family: Raleway;
  margin-top: 20px;
  margin-bottom: 10px;
  @media (max-width: 576px) {
    font-size: 25px;
  }
}
.h2 {
  font-family: Raleway;
  font-size: 32px;
  color: var(--petrol);
  @media (max-width: 576px) {
    font-size: 16px;
  }
}
.h3 {
  font-family: Raleway;
  font-size: 28px;
  color: var(--blueberry);
}

sources.scss/_header.scss:
.header {
  font-family: Raleway;
  display: flex;
  flex-direction: column;
  z-index: 1;
  height: 100%;
  text-align: center;
  color: var(--blueberry);
  box-shadow: 0 0px 3px var(--dark-blueberry);
  margin-bottom: 20px;
}
.header__button-group {
  margin-top: 5px;
}
.header__button-group a {
  text-decoration: none;
  border: 0.5px solid var(--blueberry);
  color: var(--petrol);
}
.header__button-group a:hover {
  color: var(--blueberry);
  box-shadow: inset 0 0px 5px var(--dark-bluberry-light);
}
.header__button {
  border: none;
  color: var(--blueberry);
  border-radius: 15px;
  font-size: 16px;
  background: white;
  padding: 6px;
  margin: 0 5px 10px 5px;
  box-shadow: 0 0px 5px var(--dark-bluberry-light);
}

sources.scss/_scroll-container.scss:
.scroll-container {
  display: grid;
  grid-template-rows: auto auto auto auto auto auto;
  grid-template-columns: auto auto;
  grid-template-areas:
    "image-rounded big-card"
    "image-rounded  big-card"
    "bootcamp bootcamp"
    "tags tags"
    "tables tables"
    "refs refs";
  color: var(--petrol);
  overflow-y: scroll;
  padding: 50px 120px;
  @media (min-width: 350px) {
    width: 100vw;
    padding: 16px;
  }
  @media (max-width: 576px) {
    grid-template-rows: auto auto auto auto auto auto;
    grid-template-columns: 100% auto;
    grid-template-areas:
      "image-rounded image-rounded"
      "big-card big-card"
      "bootcamp bootcamp"
      "tags tags"
      "tables tables"
      "refs refs";
    padding: 16px;
  }
}

sources.scss/_introduction.scss:
.introduction {
  display: flex;
  width: auto;
  justify-content: center;
  @media (max-width: 576px) {
    flex-direction: row;
    margin-top: 20px;
  }
}
.introduction__image-rounded {
  grid-area: image-rounded;
  border-radius: 50%;
  width: 240px;
  height: 240px;
  box-shadow: 0 0px 8px var(--dark-blueberry);
}
.introduction__h2 {
  margin-top: 16px;
  font-size: 34px;
  color: var(--blueberry);
}
.introduction__h4 {
  font-size: 22px;
  @media (max-width: 768px) {
    margin-top: 10px;
  }
}
.introduction__big-card {
  grid-area: big-card;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  width: auto;
  @media (max-width: 576px) {
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    margin-top: 25px;
    margin-left: 0px;
    border-top: 0.8px solid var(--blueberry);
    border-bottom: 0.8px solid var(--blueberry);
    padding: 25px 15px;
  }
}
.introduction__big-card a {
  text-decoration: none;
  color: var(--blueberry);
  border: 0.5px solid var(--blueberry);
  border-radius: 20px;
  font-size: 20px;
  padding: 9px;
  margin: 20px 5px;
  color: var(--dark-blueberry);
  text-align: left;
  box-shadow: 0 0px 5px var(--dark-bluberry-light);
}
.introduction__big-card a:hover {
  box-shadow: inset 0 0px 8px var(--dark-blueberry);
}

sources.scss/_bootcamp.scss:
.bootcamp {
  grid-area: bootcamp;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  font-size: 16px;
  padding: 30px;

  @media (max-width: 768px) {
    padding: 25px 15px;
  }
}
.bootcamp_text {
  display: flex;
  align-items: center;
  text-align: left;
  @media (max-width: 576px) {
    padding: 0;
    flex-direction: column;
  }
}

.bootcamp_image {
  height: 540px;
  @media (max-width: 576px) {
    height: 400px;
  }
}

.bootcamp_capstone {
  padding: 0 100px;
  @media (max-width: 576px) {
    padding: 0 0;
  }
}

sources.scss/_tags.scss:
.tags {
  grid-area: tags;
  display: flex;
  justify-content: flex-start;
  margin: 0;
  padding: 0 30px;
  box-sizing: border-box;
  @media (max-width: 768px) {
    flex-direction: column;
  }
  @media (max-width: 576px) {
    flex-direction: column;
    padding: 0;
  }
}
.tags__tag {
  flex-grow: 1;
  margin: 5px 6px;
  border: 1px solid var(--petrol);
  padding: 5px 7px;
  line-height: 3;
  color: var(--petrol);
  border-radius: 14px;
}
.tags__h3 {
  color: var(--blueberry);
  margin-top: 40px;
}
.tags__text {
  margin: 0 10px;
  @media (max-width: 576px) {
    padding: 0;
  }
}

sources.scss/_table.scss:
.table {
  grid-area: tables;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  flex: 1;
  margin: 0;
  padding: 40px;
  color: var(--petrol);
  @media (max-width: 576px) {
    flex-direction: column;
    padding: 0 10px;
  }
}
.table__table-left {
  display: flex;
  flex-direction: column;
  flex-grow: 1;
  padding-right: 20px;
  a {
    color: #ee7600;
    text-decoration: none;
    &:hover {
      color: white;
      text-decoration: none;
      background-color: #ee7600;
    }
  }
  @media (max-width: 576px) {
    padding-right: 0;
  }
}
.table__table-right {
  display: flex;
  flex-direction: column;
  flex-grow: 1;
  padding-right: 20px;
  @media (max-width: 576px) {
    padding-right: 0;
  }
}

sources.scss/_ref.scss:
.ref {
  grid-area: refs;
  width: auto;
  display: flex;
  justify-content: space-between;
  margin-bottom: 40px;
  flex-direction: column;
  padding: 30px;
  @media (max-width: 576px) {
    flex-direction: column;
    align-items: center;
    padding: 0;
  }
}
.ref_wrapper {
  display: flex;
  flex-direction: row;
  @media (max-width: 768px) {
    flex-direction: column;
  }
  @media (max-width: 576px) {
    flex-direction: column;
    align-items: center;
    padding: 0;
  }
}

.ref__card {
  display: flex;
  justify-content: space-evenly;
  flex-direction: column;
  border: 2px solid var(--petrol);
  border-radius: 10px;
  margin: 10px;

  p {
    margin: 6px;
  }
  h4 {
    margin: 6px;
  }
  @media (max-width: 768px) {
    max-width: 400px;
  }
  @media (max-width: 576px) {
    max-width: 400px;
    margin: 20px 0;
  }
}

.ref__card-image {
  border-top-left-radius: 8px;
  border-top-right-radius: 8px;
  width: 100%;
}
.ref__heading {
  align-self: flex-start;
}

sources.scss/_nav.scss:
nav {
  z-index: 1;

  box-shadow: 0 0px 3px #3a374e;
}
.nav__bottom-navigation {
  display: flex;
  justify-content: space-evenly;
  align-items: center;
}

.nav__image {
  width: 32px;
  height: 32px;
}
